(function () {
  if (!app || !app.documents || !app.documents.length) {
    alert('No document open');
    return;
  }


  var doc = app.activeDocument;
  var items = doc.pageItems;
  var found = null;


  // ===================== CONFIGURATION ====================


  // Set the preset to use (must match preset name visible in Illustrator's Image Trace panel)
  // Currently set to 'Low Fidelity Photo' preset by default
  var PRESET_NAME = 'Low Fidelity Photo';


  // Configurable fill color for Step 6 (RGB)
  // Currently set to green for easy visibility
  var FILL_COLOR = { r: 0, g: 255, b: 0 };


  // Pause after coloring the background (ms)
  // Currently set to 300 ms to allow brief viewing
  var PAUSE_AFTER_COLOR_MS = 300;

  // Remove the selected path (background) after showing color? Set true/false
  // Currently set to true to remove the selected path (background) after coloring
  var REMOVE_BG = true;
  // =================== END CONFIGURATION ==================


  // Step 1
  for (var i = 0; i < items.length; i++) {
    var ty = items[i].typename;
    if (ty === 'RasterItem' || ty === 'PlacedItem') {
      found = items[i];
      break;
    }
  }


  if (!found) {
    alert('No image found');
    return;
  }


  // Step 1
  for (var j = 0; j < items.length; j++) {
    try { items[j].selected = false; } catch (e) {}
  }
  try { found.selected = true; } catch (e) {}


  if (typeof found.trace !== 'function') {
    alert('item.trace() not available');
    return;
  }


  // Step 2
  if (typeof tracingPresetsList === 'undefined' || !tracingPresetsList.length) {
    alert('No tracingPresetsList');
    return;
  }
  var preset = null;
  for (var kk = 0; kk < tracingPresetsList.length; kk++) {
    var cand = tracingPresetsList[kk];
    var label = (cand && cand.name) ? cand.name : (cand && cand.title) ? cand.title : String(cand);
    if (label === PRESET_NAME) { preset = cand; break; }
  }
  if (!preset) { alert('Preset "' + PRESET_NAME + '" not found'); return; }


  // Step 3
  var t;
  try {
    t = found.trace();
  } catch (err) {
    alert('Trace failed: ' + (err && err.message ? err.message : err));
    return;
  }


  var tImg = t || (found.tracing ? found : null);


  if (!tImg || !tImg.tracing || !tImg.tracing.tracingOptions) {
    alert('Tracing object not found');
    return;
  }


  try {
    tImg.tracing.tracingOptions.loadFromPreset(preset);
  } catch (err) {
    alert('Failed to load preset');
    return;
  }


  // Step 4
  try {
    var expanded = tImg.tracing.expandTracing();
    try { if (expanded) expanded.selected = true; } catch (e) {}
    try { redraw(); } catch (r) {}
    try { $.sleep(100); } catch (s) {}
  } catch (err) {
    alert('Expand failed');
    return;
  }


  try { app.executeMenuCommand('ungroup'); } catch (e) {}


  // Step 5
  var bottom = (doc.layers && doc.layers.length) ? doc.layers[doc.layers.length - 1] : null;
  if (!bottom) {
    alert('No bottom layer');
    return;
  }


  try { bottom.visible = true; } catch (e) {}


  var target = null;
  if (bottom.pathItems && bottom.pathItems.length > 0) target = bottom.pathItems[bottom.pathItems.length - 1];
  else if (bottom.compoundPathItems && bottom.compoundPathItems.length > 0) target = bottom.compoundPathItems[bottom.compoundPathItems.length - 1];


  if (!target) {
    alert('No top-level path found in bottom layer (nested-only). Aborting.');
    return;
  }


  for (var s = 0; s < items.length; s++) try { items[s].selected = false; } catch (e) {}
  try { target.selected = true; } catch (e) { alert('Cannot select target'); return; }


  // Step 6
  try {
    var gcol = new RGBColor();
    gcol.red = FILL_COLOR.r;
    gcol.green = FILL_COLOR.g;
    gcol.blue = FILL_COLOR.b;
    target.filled = true;
    target.fillColor = gcol;
    target.stroked = false;
  } catch (err) {
    alert('Step 6 failed');
    return;
  }


  try { redraw(); } catch (r) {}
  try { $.sleep(PAUSE_AFTER_COLOR_MS); } catch (s) {}


  // Step 7
  if (REMOVE_BG) {
    try { target.remove(); } catch (err) { alert('Step 7 failed'); return; }
  }


  // Step 8
  try { app.executeMenuCommand('selectall'); } catch (err) { alert('Step 8 failed'); return; }


  // Step 9
  try { app.executeMenuCommand('group'); } catch (err) { alert('Step 9 failed'); return; }


})();
