// SimpleRemoveBG: thanks to Dimas for providing the base script

var doc = app.activeDocument;


// Select background layer if exists
if (doc.layers.length >= 1 && doc.layers[doc.layers.length - 1].isBackgroundLayer) {
    doc.activeLayer = doc.layers[doc.layers.length - 1];
}


// Unlock background layer
if (doc.activeLayer.isBackgroundLayer) {
    doc.activeLayer.isBackgroundLayer = false;
}


// Run remove background from quick actions
var idremoveBackground = stringIDToTypeID( "removeBackground" );
var desc2 = new ActionDescriptor();
executeAction( idremoveBackground, desc2, DialogModes.NO );

var doc = app.activeDocument;
var layer = doc.activeLayer;
var bounds = layer.bounds;
var width = bounds[2].as("px") - bounds[0].as("px");
var height = bounds[3].as("px") - bounds[1].as("px");
var centerX = doc.width.as("px") / 2;
var centerY = doc.height.as("px") / 2;
var newLeft = centerX - width / 2;
var newTop = centerY - height / 2;
var deltaX = UnitValue(newLeft - bounds[0].as("px"), "px");
var deltaY = UnitValue(newTop - bounds[1].as("px"), "px");
layer.translate(deltaX, deltaY);

// Convert active layer to Smart Object
var idnewPlacedLayer = stringIDToTypeID( "newPlacedLayer" );
var desc = new ActionDescriptor();
executeAction( idnewPlacedLayer, desc, DialogModes.NO );


// Select content of the active layer (load selection from transparency, like Ctrl+click)
var idsetd = charIDToTypeID( "setd" );
var desc2 = new ActionDescriptor();
var idnull = charIDToTypeID( "null" );
var ref = new ActionReference();
var idChnl = charIDToTypeID( "Chnl" );
var idfsel = charIDToTypeID( "fsel" );
ref.putProperty( idChnl, idfsel );
desc2.putReference( idnull, ref );
var idT = charIDToTypeID( "T   " );
var ref2 = new ActionReference();
var idChnl = charIDToTypeID( "Chnl" );
var idTrsp = charIDToTypeID( "Trsp" );
ref2.putEnumerated( idChnl, idChnl, idTrsp );
desc2.putReference( idT, ref2 );
executeAction( idsetd, desc2, DialogModes.NO );


// Crop to the selection
var idCrop = charIDToTypeID( "Crop" );
var desc3 = new ActionDescriptor();
executeAction( idCrop, desc3, DialogModes.NO );


// Deselect
app.activeDocument.selection.deselect();

// Set canvas size to 4000 x 3000 pixels, centered
var doc = app.activeDocument;
var newW = UnitValue(4000, "px");
var newH = UnitValue(3000, "px");
doc.resizeCanvas(newW, newH, AnchorPosition.MIDDLECENTER);

// Set document resolution to 300 PPI without resampling
var doc = app.activeDocument;
doc.resizeImage(undefined, undefined, 300, ResampleMethod.NONE);
