// ===== CONFIG: Ganti HEX di sini =====
var hexColor = "#FF8800";
// ======================================


// Konversi HEX â†’ RGB
function hexToRgb(hex) {
    hex = hex.replace(/^#/, "");
    if (hex.length === 3) {
        hex = hex.split("").map(function(c) { return c + c; }).join("");
    }
    return {
        r: parseInt(hex.substr(0, 2), 16),
        g: parseInt(hex.substr(2, 2), 16),
        b: parseInt(hex.substr(4, 2), 16)
    };
}


// Ubah warna Solid Fill layer
function changeSolidFillColor(hexColor) {
    var rgb = hexToRgb(hexColor);

    var desc1 = new ActionDescriptor();
    var ref1 = new ActionReference();
    ref1.putEnumerated(stringIDToTypeID("contentLayer"), charIDToTypeID("Ordn"), charIDToTypeID("Trgt"));
    desc1.putReference(charIDToTypeID("null"), ref1);

    var desc2 = new ActionDescriptor();
    var desc3 = new ActionDescriptor();
    desc3.putDouble(charIDToTypeID("Rd  "), rgb.r);
    desc3.putDouble(charIDToTypeID("Grn "), rgb.g);
    desc3.putDouble(charIDToTypeID("Bl  "), rgb.b);
    desc2.putObject(charIDToTypeID("Clr "), charIDToTypeID("RGBC"), desc3);
    desc1.putObject(charIDToTypeID("T   "), stringIDToTypeID("solidColorLayer"), desc2);

    executeAction(charIDToTypeID("setd"), desc1, DialogModes.NO);
}


// Jalankan
changeSolidFillColor(hexColor);
